DROP TABLE IF EXISTS `yii_channel`;
CREATE TABLE IF NOT EXISTS `yii_channel` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `parent_id` int(11) NOT NULL,
  `name` varchar(120) NOT NULL,
