INSERT INTO `yii_fragment2_data` (`id`, `fragment_id`, `title`, `title_format`, `title_pic`, `url`, `sub_title`, `summary`, `publish_time`, `sort_num`) VALUES
(1, 3, '媒体：云南原高官孔垂柱去世 曾被传患艾滋自杀', 'i,u,44cc', 'attachment/image/20140716/20140716091603_86146.png', '?r=content/detail&id=92&chnid=110', '', '新闻回顾：《第一财经（微博）日报》记者从云南多位人士处获知，7月13日，云南省人大常委会原副主任孔垂柱去世，去世时刚过完61岁生日。他的去世原因在云南传言甚多，更多人表示，其为再度自杀，也有人认为是因病医治无效。目前，在云南省人大常委会官方网站上的第十二届人大常委会组成人员名单中，孔垂柱的名字仍然在列。据人民网微博消息称，今年5月29日，云南省第十二届人民代表大会常务委员会第九次会议表决通过，决定接受孔垂柱辞去省十二届', '2013-03-31 16:00:00', 1),
(2, 3, '北京一主任科员伙同上司受贿176万获刑12年', '', '', '?r=content/detail&id=49&chnid=110', '', '受贿罪被警方刑事拘留。今年3月初，陈鹏被公诉至市二中院，涉案金额为176.6万元。 法院一审查明，2007年2月，陈鹏在担任交通部救助打捞局（现称交通运输部救助打捞局）救助指挥处主任科员期间，伙同救助指挥处处长朱宝柱（另案处理），利用朱宝柱负责远程可视救助指挥系统等项目的职务便利，向北京海兰信数据记录科技股份有限公司索要8万元，朱宝柱实得7万元，陈鹏实得', '2014-07-16 09:24:40', 0),
(3, 3, '许其亮:反贪惩腐重要关头军队坚决听党中央指挥', '', '', '?r=content/detail&id=72&chnid=110', '', '副主席许其亮在会上强调，要深入学习贯彻习主席系列重要讲话精神，适应新形势新任务新要求，把抓基层打基础作为永恒主题，坚持做到重心不偏、力度不减，切实夯实部队建设根基。', '2014-07-16 09:25:13', NULL),
(4, 3, '冀文林成长轨迹:曾对临终父亲称自己从不贪', '', '', '?r=content/detail&id=73&chnid=110', '', '他人谋取利益，收受、索取巨额贿赂；与他人通奸。\r\n冀文林的履历显示，36岁官至副厅，47岁升至副省，可谓顺风顺水。不过从这名副省长出身贫寒的家庭角度看，在他步入仕途前，汲取了这个农村家庭几乎全部的资源，最后却倒在了官场高处。事后他的二姐曾追悔莫及，“早知道他这样，不如当初让他在家种地。凭他的脑子在家种地也能过上', '2014-07-16 09:25:52', NULL),
(5, 5, '北京一主任科员伙同上司受贿176万获刑12年', '', 'http://images.china.cn/attachement/jpg/site1000/20140629/d02788e9b6d41519f4ac0b.jpg', '?r=content/detail&id=49&chnid=110', '', '陈鹏在担任交通部救助打捞局（现称交通运输部救助打捞局）救助指挥', '2014-07-16 09:34:44', NULL),
(6, 6, '逗妹吐槽：乌贼完胜乌鸦嘴 苏牙荣获金牙奖', 'b,u,', 'http://i3.sinaimg.cn/ty/2014/0714/U10623P6DT20140714112736.jpg', '?r=content/detail&id=89&chnid=105', '', '韩国队球衣，哦对还得准备乌兹别克斯坦，泰国，越南的……', '2014-07-16 12:11:05', 1);

